function userReducer(state = {
        name: "", 
        email: "", 
        id: "", 
        loggedIn: false,
        watchlists: []
        }, action) {
    console.log('State from userReducer:', state)
    console.log('Action from userReducer:', action)

    switch (action.type) {
        case 'LOGIN_USER':
            const newUser = {
                name: action.payload.name, 
                email: action.payload.email,
                id: action.payload.id,
                loggedIn: true,
                watchlists: action.payload.watchlists
            }

            return {...state, ...newUser}
        case 'START_USER_LOGIN':
            return state
        case 'START_ADD_WATCHLIST':
            return state
        case 'ADD_WATCHLIST':
            // debugger
            return {...state, watchlists: action.payload}    
        case 'REMOVE_WATCHLIST':
            return {...state, watchlists: action.payload} 

        case 'START_GET_DAILY_DATA':
            return state
        case 'ADD_DAILY_DATA':

            const watchlistIndex = state.watchlists.findIndex(watchlist => watchlist.id === action.payload.id)

            const newWatchlists = [...state.watchlists, state.watchlists.splice(watchlistIndex, 1, action.payload)]
            debugger
            return state
        default:
            return state
    } 
}

export default userReducer
