import React from 'react'
import Form from 'react-bootstrap/Form'


export default class Login extends React.Component {
    // console.log('props from login: ', this.props)

    state = {
        name: "",
        email: "",
        password: ""
    }

    handleChange = (event) => {
        // debugger
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    returnLogin = (event) => {
        this.props.loginUser(this.state)
    }

    render() {
        return(
            <div>
                <Form className='login-form'>
                    <Form.Group controlId="formBasicText">
                    <Form.Label>Full Name: </Form.Label>
                        <Form.Control type='text' onChange={this.handleChange} name='name' placeholder='Enter full name' value={this.state.name} />
                    </Form.Group>
                    <Form.Group controlID='formBasicEmail'>
                        <Form.Label>Email: </Form.Label>
                        <Form.Control type='email' onChange={this.handleChange} name='email' placeholder='Enter email' value={this.state.email} />
                        <Form.Text className='text-muted'>
                            Email address is used for login
                        </Form.Text>
                    </Form.Group>
                    <Form.Group controlId="formBasicPassword">
                        <Form.Label>Password:</Form.Label>
                        <Form.Control type="password" onChange={this.handleChange} name='password' placeholder="Enter password" value={this.state.password} />
                        <Form.Text className='text-muted'>
                            Password is encripted using Bcrypt GEM
                        </Form.Text>
                    </Form.Group>

                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
                </Form>
            </div>




        )
    }
}
