import React from 'react'
import { connect } from 'react-redux'

import Form from 'react-bootstrap/Form'
import Dropdown from 'react-bootstrap/Dropdown'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Button from 'react-bootstrap/Button'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'

class DailyForm extends React.Component {

    class = {
        startDate: '2020-07-01',
        endDate: ''
    }

    addToWatchlist = () => {
        if (this.props.data.ticker && this.props.user.watchlists.length > 0) {
            return (
                <DropdownButton title="Add to Watchlist">
                    {this.props.user.watchlists.map(watchlist => {
                        return (<Dropdown.Item
                            onClick={()=>{this.addCompany(watchlist.id, this.props.data.ticker)}}
                            >{watchlist.name}</Dropdown.Item>)
                    })}    
                </DropdownButton>
            )
        }}

    render() {
    return (
        <div>
        <h2>Daily Form Component</h2>

        <Form>
        <Form.Group >
                            <Form.Label>Date Range:</Form.Label>
                            <Row>
                            <Col>
                                <Form.Label>Start Date:</Form.Label>
                                <Form.Control type="date" onChange={this.handleChange} name='startDate' value={this.state.startDate} />
                                <Form.Text className='text-muted'>
                                    Start Date for Chart
                                </Form.Text>
                            </Col>
                            <Col>
                            <Form.Label>End Date:</Form.Label>
                                <Form.Control type="date" onChange={this.handleChange} name='endDate' value={this.state.endDate} />
                                <Form.Text className='text-muted'>
                                    End Date for Chart
                                </Form.Text>
                            </Col>
                            </Row>
                        </Form.Group>
                        </Form>
                        </div>
    )
    }
}

export default connect()(DailyForm)
